import React, { useState } from 'react';
import {
  compose,
  bindActionCreators,
  AnyAction,
  Dispatch,
} from 'redux';
import { IMatch } from '../../Interfaces';
import {
  getIn,
  Record,
  List,
} from 'immutable';
import { connect } from 'react-redux';
import {
  Grid,
  Typography,
  Button,
  TextField,
  Checkbox
} from '@material-ui/core';
import {
  AddTodoAction,
  DeleteTodoAction,
  CompletedTodoAction,
  AddSubTaskAction,
  ITodo,
  ISubTask,
  TodoFactory,
  SubTaskFactory,
  IUser,
} from '../../actions/default';
import {
  makeSelectTodosForUser,
  makeSelectUser,
} from '../../selectors/default';
import { createStructuredSelector } from 'reselect';

interface ITodoComponentProps {
  match: IMatch,
}

interface ITodoProps extends ITodoComponentProps {
  addTodo: (userId: number, todo: Record<ITodo>) => void;
  addTodoSubTask: (todo: Record<ITodo>) => void;
  deleteTodo: (todoId: number) => void;
  completedTodo: (todo: Record<ITodo>, complete: boolean) => void;
  addSubTask: (todoId: number, subtask: Record<ISubTask>) => void;
  userId: number;
  todoId: number;
  todosForUser: List<Record<ITodo>>;
  user?: Record<IUser>;
}

const addTodo = (userId: number, todo: Record<ITodo>) => new AddTodoAction({ userId, todo });
const deleteTodo = (todo: Record<ITodo>) => new DeleteTodoAction({ todo });
const completedTodo = (todo: Record<ITodo>, complete: boolean) => new CompletedTodoAction({ todo, complete });
const addSubTask = (todoId: number, subtask: Record<ISubTask>) => new AddSubTaskAction({ todoId, subtask });

const Todo: React.FC<ITodoProps> = (props) => {
  const [textInput, setTextInput] = useState('');
  const [toggleAddInput, setToggleAddInput] = useState(false);

  const {
    addTodo,
    deleteTodo,
    completedTodo,
    addSubTask,
    userId,
    todoId,
    todosForUser,
    user,
  } = props;
  console.log('TodoUser', props)
  if (user == null) {
    return (
      <Grid
        container={true}
        direction='column'
        wrap='nowrap'
      >
        <Grid
          item={true}
        >
          <Typography
            variant='h5'
          >
            INVALID USER
          </Typography>
        </Grid>
      </Grid>
    );
  }
  return (
    <Grid
      container={true}
      direction='column'
      wrap='nowrap'
    >
      <Grid
        item={true}
      >
        <Typography
          variant='h5'
        >
          TODOS FOR {user.get('name')}
        </Typography>
      </Grid>
      <Grid
        container={true}
        item={true}
        direction='column'
        wrap='nowrap'
      >
      { toggleAddInput ?
        <Grid
          item={true}
          container={true}
          alignItems='center'
        >
          <Grid
            item={true}
          >
            <TextField
              label='title'
              value={textInput}
              onChange={(e) => {
                setTextInput(e.target.value);
              }}
            />
          </Grid>
          <Grid
            item={true}
          >
            <Button
              variant='outlined'
              onClick={
                () => {
                  addTodo(
                    userId,
                    TodoFactory({
                      title: textInput,
                    }),
                  );
                  setTextInput('');
                }
              }
            >
              Add Todo
            </Button>
          </Grid>

        </Grid>
      :  
        <Grid
          item={true}
          container={true}
          alignItems='center'
        >
          <Grid
            item={true}
          >
            <TextField
              label='title'
              value={textInput}
              onChange={(e) => {
                setTextInput(e.target.value);
              }}
            />
          </Grid>
          <Grid
            item={true}
          >
            <Button
              variant='outlined'
              onClick={
                () => {
                  addSubTask(
                    todoId,
                    SubTaskFactory({
                      title: textInput,
                    }),
                  );
                  setTextInput('');
                }
              }
            >
              Add SubTask
            </Button>
          </Grid>

        </Grid>}
        {
          todosForUser.map((todo, index) => {
            let complete = todo.get('complete')
            return 
            <Card className={classes.card}>
              <CardActionArea>
                <CardMedia
                  component="img"
                  alt="Contemplative Reptile"
                  height="140"
                  image="/static/images/cards/contemplative-reptile.jpg"
                  title="Contemplative Reptile"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    Lizard
                  </Typography>
                  <Typography variant="body2" color="textSecondary" component="p">
                    Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                    across all continents except Antarctica
                  </Typography>
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  Share
                </Button>
                <Button size="small" color="primary" onClick={() => setToggleAddInput(!toggleAddInput)}>
                  Learn More
                </Button>
              </CardActions>
            </Card>

           {/* <Grid
              key={index}
              item={true}
            >
            <Checkbox
                checked={complete}
                value={complete}
                onChange={() => completedTodo(todo, complete)}
                inputProps={{
                  'aria-label': 'primary checkbox',
                }}
              />
              {todo.get('title')}
              <Button  
                variant='outlined'
                //@ts-ignore       
                onClick={() => deleteTodo(todo)}>            
                Delete Todo
              </Button> 
              <Button  
                variant='outlined'      
                onClick={() => setToggleAddInput(!toggleAddInput)}
                >            
                {toggleAddInput ? <p>Add SubTask</p> : <p>Adding SubTask</p>}
              </Button> 
              </Grid>*/}
          })
        }
      </Grid>
    </Grid>
  );
}

const mapStateToProps = (state: any, props: ITodoComponentProps) => {
  const {
    match,
  } = props;
  const userId = parseInt(getIn(match, ['params', 'userId'], -1), 10); // from path / router
  return {
    userId,
    ...createStructuredSelector({
      todosForUser: makeSelectTodosForUser(userId),
      user: makeSelectUser(userId),
    })(state)
  }
};

const mapDispatchToProps = (dispatch: Dispatch<AnyAction>) => {
  return {
    ...bindActionCreators({ addTodo, deleteTodo, completedTodo, addSubTask }, dispatch)
  };
};


export default compose<React.ComponentClass<ITodoComponentProps>>(
  connect(mapStateToProps, mapDispatchToProps)
)(Todo);
