import {
  fromJS,
  Record,
  Map,
} from 'immutable';
import {
  IAction,
} from '../Interfaces';

import {
  DefaultActionTypes,
  ITodo,
  AddTodoAction,
  AddSubTaskAction,
  DeleteTodoAction,
  CompletedTodoAction,
  IUser,
  AddUserAction,
  DeleteUserAction,
  UserFactory,
  TodoFactory,
} from '../actions/default';
import { useScrollTrigger } from '@material-ui/core';
import TodoUser from '../pages/TodoUser';

// import {
//   Settings,
// } from '../../models';

export interface IReducerState {
  lastUserId: number;
  lastTodoId: number;
  lastSubTaskId: number;
  users: Map<number, Record<IUser>>;
  todos: Map<number, Record<ITodo>>;
}

const initialUsers = [
  UserFactory({
    id: 1,
    name: 'Ryan',
  }),
  UserFactory({
    id: 2,
    name: 'Sandy',
  }),
  UserFactory({
    id: 3,
    name: 'Sean',
  }),
  UserFactory({
    id: 4,
    name: 'Peter',
  }),
]

const initialTodos = [
  TodoFactory({
    id: 1,
    userId: 1,
    title: 'Drink Water',
    complete: false
  })
]
const INITIAL_STATE = fromJS({
  lastUserId: initialUsers.length,
  lastTodoId: initialTodos.length,
  lastSubTaskId: 0,
  users: Map<number, Record<IUser>>().withMutations((mutableMap) => {
    initialUsers.forEach((user) => {
      mutableMap.set(user.get('id'), user);
    })
  }),
  todos: Map<number, Record<ITodo>>().withMutations((mutableMap) => {
    initialTodos.forEach((todo) => {
      mutableMap.set(todo.get('id'), todo);
    })
  }),
});

export const reducer = (state: Record<IReducerState> = INITIAL_STATE, action: IAction) => {
  console.log('state', state)
  console.log('action', action)
  switch (action.type) {

    case DefaultActionTypes.ADD_USER: {
      const lastUserId = state.get('lastUserId');
      const {
        payload,
      } = action as AddUserAction;
      const {
        user,
      } = payload;

      if (user.get('name') === '') {
        console.debug('no name!')
        return state;
      }

      const userId = lastUserId + 1;
      return state.withMutations((mutableState) => {
        mutableState.set('lastUserId', userId);
        mutableState.setIn(
          ['users', userId],
          user.set('id', userId),
        );
      })
    }

    case DefaultActionTypes.DELETE_USER: {
      const {
        payload,
      } = action as DeleteUserAction;
      const {
        userId,
      } = payload;

      return state.withMutations((map) => {
        console.log(map)
        map
          .removeIn(['users', userId])
          .removeIn(['todos', userId])
      });
    }

    case DefaultActionTypes.ADD_TODO: {
      const lastTodoId = state.get('lastTodoId');
      const {
       payload,
      } = action as AddTodoAction;
      const {
       userId,
       todo,
      } = payload;

      if (todo.get('title') === '') {
        console.debug('no title!')
        return state;
      }

      const todoId = lastTodoId + 1;

      return state.withMutations((mutableState) => {
        mutableState.set('lastTodoId', todoId);
        mutableState.setIn( 
          ['todos', todoId],
          todo.withMutations((mutableTodo) => {
            mutableTodo.set('id', todoId)
            mutableTodo.set('userId', userId)
          }),
        );
      });
    }

    case DefaultActionTypes.ADD_SUBTASK: {
      const lastSubTaskId = state.get('lastSubTaskId');
      const {
       payload,
      } = action as AddSubTaskAction;
      const { 
       subtask,
       todoId
      } = payload;
      console.log('todoId', todoId)
      console.log('subtask', subtask)

      const subtaskId = lastSubTaskId + 1;

      // return state.withMutations((mutableState) => {
      //  mutableState.set('todos', todoId);
      // let subTasks = "Joosie"
      //  mutableState.setIn( 
      //    ['subtasks', todoId],
      //    subtask.withMutations((mutableTodo) => {
      //      mutableTodo.set('id', todoId)
      //      mutableTodo.set('subtasks', "josie")
      //    }),
      //  );
      // });
      return state;
    }

    case DefaultActionTypes.COMPLETE_TODO: {
      const {
        payload,
      } = action as CompletedTodoAction;
      const {
        todo,
        complete
      } = payload;
      let id = todo.get('id')
      return state.withMutations((mutableState) => {
        mutableState.setIn( 
        ['todos', id],
        todo.withMutations((mutableTodo) => {
          mutableTodo.set('complete', !complete)
        }),
      );
      })
    }

    case DefaultActionTypes.DELETE_TODO: {
      const {
        payload,
      } = action as DeleteTodoAction;
      const {
        todo,
      } = payload;
      return state.withMutations((map) => {
        let id = todo.get('id')
        map
          .removeIn(['todos', id])
      });
    }

    default:
      return state;
  }
};

export default reducer;
