import React, { useState } from 'react';
import {
  compose,
  bindActionCreators,
  AnyAction,
  Dispatch,
} from 'redux';
import { IMatch } from '../../Interfaces';
import {
  getIn,
  Record,
  List,
} from 'immutable';
import { connect } from 'react-redux';
import {
  Grid,
  Typography,
  Button,
  TextField,
  Checkbox
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import {
  AddTodoAction,
  DeleteTodoAction,
  CompletedTodoAction,
  AddSubtaskAction,
  ITodo,
  ISubtask,
  TodoFactory,
  SubtaskFactory,
  IUser,
} from '../../actions/default';
import {
  makeSelectTodosForUser,
  makeSelectUser,
} from '../../selectors/default';
import { createStructuredSelector } from 'reselect';

interface ITodoComponentProps {
  match: IMatch,
}

interface ITodoProps extends ITodoComponentProps {
  addTodo: (userId: number, todo: Record<ITodo>) => void;
  addTodoSubtask: (todo: Record<ITodo>) => void;
  deleteTodo: (todoId: number) => void;
  completedTodo: (todo: Record<ITodo>, complete: boolean) => void;
  addSubtask: (todoId: number, subtask: Record<ISubTask>) => void;
  userId: number;
  todoId: number;
  todosForUser: List<Record<ITodo>>;
  user?: Record<IUser>;
}

const addTodo = (userId: number, todo: Record<ITodo>) => new AddTodoAction({ userId, todo });
const deleteTodo = (todo: Record<ITodo>) => new DeleteTodoAction({ todo });
const completedTodo = (todo: Record<ITodo>, complete: boolean) => new CompletedTodoAction({ todo, complete });
const addSubtask = (todoId: number, subtask: Record<ISubTask>) => new AddSubTaskAction({ todoId, subtask });

const useStyles = makeStyles({
  card: {
    maxWidth: 345,
  },
});

const Todo: React.FC<ITodoProps> = (props) => {
  const classes = useStyles();
  const [textInput, setTextInput] = useState('');
  const [toggleAddInput, setToggleAddInput] = useState(false);
  const [currentTodoId, setCurrentTodoId ] = useState(0);
  const {
    addTodo,
    deleteTodo,
    completedTodo,
    addSubTask,
    userId,
    todosForUser,
    user,
  } = props;

  console.log('TodoUser', props)
  if (user == null) {
    return (
      <Grid
        container={true}
        direction='column'
        wrap='nowrap'
      >
        <Grid
          item={true}
        >
          <Typography
            variant='h5'
          >
            INVALID USER
          </Typography>
        </Grid>
      </Grid>
    );
  }
  return (
    <Grid
      container={true}
      direction='column'
      wrap='nowrap'
    >
      <Grid
        item={true}
      >
        <Typography
          variant='h5'
        >
          TODOS FOR {user.get('name')}
        </Typography>
      </Grid>
      <Grid
        container={true}
        item={true}
        direction='column'
        wrap='nowrap'
      >
      { !toggleAddInput ?
        <Grid
          item={true}
          container={true}
          alignItems='center'
        >
          <Grid
            item={true}
          >
            <TextField
              label='title'
              value={textInput}
              onChange={(e) => {
                setTextInput(e.target.value);
              }}
            />
          </Grid>
          <Grid
            item={true}
          >
            <Button
              variant='outlined'
              onClick={
                () => {
                  addTodo(
                    userId,
                    TodoFactory({
                      title: textInput,
                    }),
                  );
                  setTextInput('');
                }
              }
            >
              Input Todo
            </Button>
          </Grid>

        </Grid>
      :  
        <Grid
          item={true}
          container={true}
          alignItems='center'
        >
          <Grid
            item={true}
          >
            <TextField
              label='title'
              value={textInput}
              onChange={(e) => {
                setTextInput(e.target.value);
              }}
            />
          </Grid>
          <Grid
            item={true}
          >
            <Button
              variant='outlined'
              onClick={
                () => {
                  addSubTask(
                    currentTodoId,
                    SubTaskFactory({
                      title: textInput,
                    }),
                  );
                  setTextInput('');
                }
              }
            >
              Input Sub Task
            </Button>
          </Grid>

        </Grid>}
        {
          todosForUser.map((todo, index) => {
            let complete = todo.get('complete')
            return (
              <Card className={classes.card} key={index}>
                <CardActionArea>
                <Checkbox
                  checked={complete}
                  value={complete}
                  onChange={() => completedTodo(todo, complete)}
                  inputProps={{
                    'aria-label': 'primary checkbox',
                  }}
                />
                  <CardContent>

                    <Typography variant="body2" color="textSecondary" component="p">
                    {todo.get('title')}
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button 
                    size="small" 
                    color="primary" 
                    //@ts-ignore   
                    onClick={() => deleteTodo(todo)}
                  >
                    Delete
                  </Button>
                  <Button 
                    size="small" 
                    color="primary" 
                    onClick={() => {
                      setToggleAddInput(!toggleAddInput);
                      setCurrentTodoId(todo.get('id'))
                    }}>
                    {!toggleAddInput ? "Add Subtask" : "Add New Task" }
                  </Button>
                </CardActions>
              </Card>
            )
          })
        }
      </Grid>
    </Grid>
  );
}

const mapStateToProps = (state: any, props: ITodoComponentProps) => {
  const {
    match,
  } = props;
  const userId = parseInt(getIn(match, ['params', 'userId'], -1), 10); // from path / router
  return {
    userId,
    ...createStructuredSelector({
      todosForUser: makeSelectTodosForUser(userId),
      user: makeSelectUser(userId),
    })(state)
  }
};

const mapDispatchToProps = (dispatch: Dispatch<AnyAction>) => {
  return {
    ...bindActionCreators({ addTodo, deleteTodo, completedTodo, addSubTask }, dispatch)
  };
};


export default compose<React.ComponentClass<ITodoComponentProps>>(
  connect(mapStateToProps, mapDispatchToProps)
)(Todo);
